import { counter } from "@fortawesome/fontawesome-svg-core";
import anime, { random } from "animejs";
import moment from "moment";
import React, { useEffect, useState } from "react";
import Anime from "react-anime";
import styled from "styled-components";


const CountdownContainer = styled.div`
  text-align: center;
  color: ${props => props.theme.primary};
  position: absolute;
  top: 50%;
  left: 50%;
  -moz-transform: translateX(-50%) translateY(-50%);
  -webkit-transform: translateX(-50%) translateY(-50%);
  transform: translateX(-50%) translateY(-50%);
  font-size: xx-large;
`

const BigPoop = styled.div`
  position: absolute;
  font-size: 100px;
  top: ${props => props.y + "px"};
  left: ${props => props.x + "px"};
  overflow: hidden;
`

const PoopContainer = styled.div`
  width: 100%;
  height: 100%;
  overflow: hidden;
`

const Poop = () => {
  useEffect(() => {
    anime({
      targets: ".poop0",
      direction: 'alternate',
      loop: true,
      easing: 'easeInOutSine',
      duration: 1000,
      top: "75%",
    })
    anime({
      targets: ".poop1",
      direction: 'alternate',
      loop: true,
      easing: 'easeInOutSine',
      duration: 1000,
      translateX: 1000,
    })
    anime({
      targets: ".poop2",
      direction: 'alternate',
      loop: true,
      easing: 'easeInOutSine',
      duration: 1000,
      translateY: -1000,
    })
    anime({
      targets: ".poop3",
      direction: 'alternate',
      loop: true,
      easing: 'easeInOutSine',
      duration: 1000,
      translateX: -1000,
    })
  }, []);

  return <>
    <BigPoop className="poop0">ðŸ’©</BigPoop>
    <BigPoop className="poop1" y={1000}>ðŸ’©</BigPoop>
    <BigPoop className="poop2" x={1000} y={1000}>ðŸ’©</BigPoop>
    <BigPoop className="poop3" x={1000}>ðŸ’©</BigPoop>
  </>
}

const Countdown = () => {
  const targetTime = moment('20231204 07:00', 'YYYYMMDD hh:mm')
  const [currTime, setCurrTime] = useState(moment())

  useEffect(() => {
    const interval = setInterval(() => setCurrTime(moment(), 1000))
    return () => {
      clearInterval(interval)
    }
  }, [])

  let diff = moment.duration(targetTime.diff(currTime))
  const days = Math.floor(diff.asDays())
  diff = diff.subtract(days, 'd')
  const hours = Math.floor(diff.asHours())
  diff = diff.subtract(hours, 'h')
  const minutes = Math.floor(diff.asMinutes())
  diff = diff.subtract(minutes, 'm')
  const seconds = Math.floor(diff.asSeconds())

  return <>
    <PoopContainer>
      <Poop />
    </PoopContainer>
    <CountdownContainer>
      <div>Time left until I see my schmoop <span className="poop">ðŸ’©</span></div>
      <div>{days} days {hours} hours {minutes} minutes {seconds} seconds</div>
    </CountdownContainer>
  </>
}

export default Countdown
